function Alerting({children}) {
  return (
    <View
      style={{backgroundColor: 'blue'}}
      onPointerDown={() => alert('Hello World!')}>
      {children}
    </View>
}

function Container() {
  return (
    <View style={styles.container}>
      // highlight-next-line
      <Alerting>
        <Widget />
      </Alerting>
    </View>
  );
}
